%spark

// Size threshold on the words of *title* field
val titleThres = z.input("title_word_threshold", 8.0)

// Size threshold of the words on *authors* field
val authorsThres = z.input("authors_word_threshold", 100.0)

%spark

// Helper: whitespace split words, keep words > threshold # of chars
def tokenizer(s: String, threshold: Int, comb: String = "OR"): String = {
    s.split(" ").map(_.replaceAll("[^a-zA-Z0-9]", "")).filter(_.length > threshold).mkString(s" ${comb} ")
}

///////////////////////////////////////////////////////////////////////////////////////
// Entity linkage function definition, i.e., how do you want your records to be linked?
//
// Input: org.apache.spark.sql.Row
// Output: Lucene query string as in [query string] (https://www.elastic.co/guide/en/elasticsearch/reference/current/query-dsl-query-string-query.html)
//////////////////////////////////////////////////////////////////////////////////////
val linker: Row => String = { case row => {

    // Get title and authors fields
    val title = row.getString(row.fieldIndex("title"))
    val authors = row.getString(row.fieldIndex("authors"))

    val titleTokens = tokenizer(title, Integer.parseInt(titleThres.toString)) // Replace 8 with 3
    val authorsTerms = tokenizer(authors, Integer.parseInt(authorsThres.toString)) // Use 3 to get 0.97 accuracy

    if (titleTokens.nonEmpty && authorsTerms.nonEmpty) {
        s"(title:(${titleTokens})) OR (authors:${authorsTerms})"
    }
    else if (titleTokens.nonEmpty){
        s"title:(${titleTokens})"
    }
    else if ( authorsTerms.nonEmpty) {
        s"(authors:${authorsTerms})"
    }
    else {
        "*:*"
    }
  }
}

//////////////////////////////////
// Entity linkage                /
//////////////////////////////////
val linkedResults = dblp2.linkDataFrame(acmDF, linker, 3)

////////////////////////////////////////
// The rest is for computing accuracy  /
////////////////////////////////////////

// Linkage Results (get top result)
val linkageResultsDF = spark.createDataFrame(linkedResults.filter(_._2.nonEmpty).map{ case (acm, topDocs) => (topDocs.head.doc.textField("id").get, acm.getInt(acm.fieldIndex("id")).toString)}).toDF("idDBLP", "idACM")

// Compute the number of currect results based on ground truth
val correctHits: Double = linkageResultsDF.join(groundTruthDF, groundTruthDF.col("idDBLP").equalTo(linkageResultsDF("idDBLP")) &&  groundTruthDF.col("idACM").equalTo(linkageResultsDF("idACM"))).count

// Total # of ground truth records    
val total: Double = groundTruthDF.count

// Accuracy
val accuracy = correctHits / total

println("*" * 40)
println("*" * 40)
println(s"* Accuracy of linkage is ${accuracy}")
println("*" * 40)
println("*" * 40)
