def tokenizer(s: String, threshold: Int, comb: String = "OR"): String = {
    s.split(" ").map(_.replaceAll("[^a-zA-Z0-9]", "")).filter(_.length > threshold).mkString(s" ${comb} ")
}

// Entity linkage function definition, i.e., how do you want your records to be linked?
// Input: org.apache.spark.sql.Row
// Output: Lucene query string as in [query string](https://www.elastic.co/guide/en/elasticsearch/reference/current/query-dsl-query-string-query.html)
val linker: Row => String = {
  case row: Row => {
    val nameTokens = tokenizer(row.getString(row.fieldIndex("name")), 2)
    val descTerms = tokenizer(row.getString(row.fieldIndex("description")), 7)

    if (descTerms.nonEmpty && nameTokens.nonEmpty) {
      s"(name:(${nameTokens})) OR (description:${descTerms})"
    }
    else if (nameTokens.nonEmpty) {
        s"(name:(${nameTokens}))"

    } 
    else {
        "*:*"
    }

   /*
   if (nameTokens.nonEmpty) {
      s"name:(${nameTokens})"
    }
    else {
      "*:*"
    }*/
  }
}

def linkedResultToIds(left: Row, topDocs: Array[SparkScoreDoc]): (Long, Long) = {
  (topDocs.head.doc.numericField("id").head.longValue(), left.getInt(left.fieldIndex("id")))
}

val linkageResults = linkedResults.map{ case (left, topDocs) => linkedResultToIds(left, topDocs)}.toDF("idBuy", "idAbt")
